import React from 'react'
import { withRouter } from 'react-router-dom'
import Card from "../../components/card"
import FormGroup from '../../components/form-group'
import SelectMenu from '../../components/selectMenu'
import EntryList from './entryList'
import EntryService from '../../app/services/entryService'
import LocalStorageService from '../../app/services/localStorageService'
import { errorAlert, successAlert } from "../../components/toastr";

class Entries extends React.Component {

  state = {
    year: '',
    month: '',
    type: '',
    status: '',
    entries: []
  }

  constructor() {
    super()
    this.service = new EntryService()
  }

  search = () => {
    const currentUser = LocalStorageService.getItem('_usuario_logado')

    this.service.filter({
      usuario: currentUser.id,
      year: this.state.year,
      month: this.state.month,
      type: this.state.type,
      status: this.state.status
    }).then(response => {
      successAlert('Data fetched!')
      this.setState({ entries: response.data })
    }).catch(error => {
      errorAlert(error.response.data)
    })
  }

  render() {
    const months = [
      { label: 'Choose...', value: '' },
      { label: 'January', value: 1 },
      { label: 'February', value: 2 },
      { label: 'March', value: 3 },
      { label: 'April', value: 4 },
      { label: 'May', value: 5 },
      { label: 'June', value: 6 },
      { label: 'July', value: 7 },
      { label: 'August', value: 8 },
      { label: 'September', value: 9 },
      { label: 'October', value: 10 },
      { label: 'November', value: 11 },
      { label: 'December', value: 12 }
    ]

    const types = [
      { label: 'Choose...', value: '' },
      { label: 'Despesa', value: 'DESPESA' },
      { label: 'Receita', value: 'RECEITA' }
    ]

    return (
      <Card title="Search Entries">
        <div className="row">
          <div className="col-lg-6">
            <div className="bs-component">
              <fieldset>
                <FormGroup label="Ano: *" htmlFor="inputYear">
                  <input type="text"
                    className="form-control"
                    value={this.state.year}
                    onChange={e => this.setState({ year: e.target.value })}
                    id="inputYear"
                    placeholder="Year"
                  />
                </FormGroup>
                <FormGroup label="Month: *" htmlFor="inputMonth">
                  <SelectMenu
                    id="inputMonth"
                    value={this.state.month}
                    onChange={e => this.setState({ month: e.target.value })}
                    className="form-control"
                    options={months}
                  />
                </FormGroup>
                <FormGroup label="Type: " htmlFor="inputType">
                  <SelectMenu
                    id="inputType"
                    value={this.state.type}
                    onChange={e => this.setState({ type: e.target.value })}
                    className="form-control"
                    options={types}
                  />
                </FormGroup>
                <button type="button" className="btn btn-success" onClick={this.search}>Buscar</button>
                <button type="button" className="btn btn-danger">Cadastrar</button>
              </fieldset>
            </div>
          </div>
        </div>
        <br />
        <div className="row">
          <div className="col-lg-12">
            <EntryList entries={this.state.entries} />
          </div>
        </div>
      </Card>
    )
  }
}

export default withRouter(Entries)
