import React from "react"
import Card from "../components/card"
import FormGroup from "../components/form-group"
import { withRouter } from "react-router-dom"
import UsuarioService from "../app/services/usuarioService"
import { errorAlert, successAlert } from "../components/toastr";

class CadastroUsuario extends React.Component {
  state = {
    nome: '',
    email: '',
    senha: '',
    senhaRepeticao: ''
  }

  constructor() {
    super()
    this.service = new UsuarioService()
  }

  salvar = () => {
    const user = {
      nome: this.state.nome,
      email: this.state.email,
      senha: this.state.senha
    }

    this.service.save(
      user
    ).then(response => {
      successAlert("The user was added! Sign in to access your profile.")
    })
  }

  cancelar = () => {
    this.props.history.push('/login')
  }

  render() {
    return (
      <Card title="Cadastro de Usuário">
        <div className="row">
          <div className="col-lg-12">
            <fieldset>
              <FormGroup label="Nome: *" htmlFor="exampleInputNome">
                <input type="email"
                  value={this.state.nome}
                  onChange={e => this.setState({ nome: e.target.value })}
                  className="form-control"
                  id="exampleInputNome"
                  aria-describedby="emailHelp"
                  placeholder="Digite o Nome" />
              </FormGroup>
              <FormGroup label="Email: *" htmlFor="exampleInputEmail1">
                <input
                  type="email"
                  value={this.state.email}
                  onChange={e => this.setState({ email: e.target.value })}
                  className="form-control"
                  id="exampleInputEmail1"
                  aria-describedby="emailHelp"
                  placeholder="Digite o Email" />
                <small id="emailHelp" className="form-text text-muted">Não divulgamos o seu email.</small>
              </FormGroup>
              <FormGroup label="Senha: *" htmlFor="exampleInputPassword1">
                <input
                  type="password"
                  value={this.state.senha}
                  onChange={e => this.setState({ senha: e.target.value })}
                  className="form-control"
                  id="exampleInputPassword1"
                  placeholder="Password" />
              </FormGroup>
              <FormGroup label="Repita a Senha: *" htmlFor="exampleInputPassword2">
                <input
                  type="password"
                  value={this.state.senhaRepeticao}
                  onChange={e => this.setState({ senhaRepeticao: e.target.value })}
                  className="form-control"
                  id="exampleInputPassword2"
                  placeholder="Password" />
              </FormGroup>
              <button type="button" className="btn btn-success" onClick={this.salvar}>Salvar</button>
              <button type="button" className="btn btn-danger" onClick={this.cancelar}>Voltar</button>
            </fieldset>
          </div>
        </div>
      </Card>
    )
  }
}
export default withRouter(CadastroUsuario)
