import React from "react";
import { HashRouter, Switch, Route, Redirect } from "react-router-dom";
import Login from "../views/login";
import CadastroUsuario from "../views/cadastroUsuario";
import Home from "../views/home";
import Entries from "../views/entries/entries";
import addEntry from "../views/entries/addEntry";
import AuthService from "../app/services/authService";

function AuthRoute({ component: Component, ...props }) {
  return (
    <Route
      {...props}
      render={(componentProps) => {
        if (AuthService.isAuthenticatedUser) {
          return <Component {...componentProps} />;
        } else {
          return (
            <Redirect
              to={{
                pathname: "/login",
                state: { from: componentProps.location },
              }}
            />
          );
        }
      }}
    />
  );
}

function Rotas() {
  return (
    <HashRouter>
      <Switch>
        <Route path="/cadastro-usuario" component={CadastroUsuario} />
        <Route path="/login" component={Login} />
        <AuthRoute path="/home" component={Home} />
        <AuthRoute path="/entries" component={Entries} />
        <AuthRoute path="/add-entry/:id?" component={addEntry} />
      </Switch>
    </HashRouter>
  );
}

export default Rotas;
