import LocalStorageService from "./LocalStorageService";

export const CURRENTUSER = "_current_user";

export default class AuthService {
	static isUserAuthenticated() {
		const currentyUser = LocalStorageService.getItem(CURRENTUSER);
		return currentyUser && currentyUser.id;
	}

	static removeAuthenticatedUser() {
		LocalStorageService.deleteItem(CURRENTUSER);
	}

	static signIn(user) {
		LocalStorageService.addItem(CURRENTUSER, user);
	}

	static getAuthenticatedUser() {
		return LocalStorageService.getItem(CURRENTUSER);
	}
}
