import React from 'react'
import { withRouter } from 'react-router-dom'
import EntryService from '../../app/services/entryService'
import Card from '../../components/card'
import FormGroup from '../../components/form-group'
import SelectMenu from '../../components/selectMenu'
import LocalStorageService from '../../app/services/localStorageService'
import { successAlert, errorAlert } from '../../components/toastr'

class AddReact extends React.Component {
  state = {
    id: null,
    descricao: '',
    ano: '',
    mes: '',
    valor: '',
    tipo: '',
    status: '',
    usuario: '',
    updating: false
  }

  constructor() {
    super()
    this.service = new EntryService()
  }

  componentDidMount() {
    const params = this.props.match.params

    if (params.id) {
      this.service.find(
        params.id
      ).then(response => {
        this.setState({ ...response.data, updating: true })
      }).catch(error => {
        errorAlert(error.response.data)
      })
    }

  }

  handleChange = (event) => {
    const value = event.target.value
    const name = event.target.name

    this.setState({ [name]: value })
  }

  validate = (entry) => {
    try {
      this.service.validate(entry)
    } catch (exception) {
      const messages = exception.messages
      messages.forEach(message => errorAlert(message))
      return false
    }
    return true
  }

  submit = () => {
    const currencyUser = LocalStorageService.getItem('_usuario_logado')
    const { descricao, ano, mes, valor, tipo } = this.state
    const entry = { descricao, ano, mes, valor, tipo, usuario: currencyUser.id }

    if (!this.validate(entry)) {
      return false
    }

    this.service.save(
      entry
    ).then(response => {
      successAlert('Entry added.')
      this.goToEntries()
    }).catch(error => {
      errorAlert(error.response.data)
    })
  }

  update = () => {
    const { descricao, ano, mes, valor, tipo, status, usuario, id } = this.state
    const entry = { descricao, ano, mes, valor, tipo, status, usuario, id }

    if (!this.validate(entry)) {
      return false
    }

    this.service.update(
      entry
    ).then(response => {
      successAlert('Entry has updated')
      this.props.history.push('/entries')
    }).catch(error => {
      errorAlert(error.response.data)
    })
  }

  goToEntries = () => {
    this.props.history.push('/entries')
  }

  render() {
    const months = this.service.getMonthList()
    const types = this.service.getTypeList()

    return (
      <Card title={this.state.updating ? 'Update Entry' : 'Add Entry'}>
        <div className="row">
          <div className="col-md-12">
            <FormGroup id="inputDescription" label="Description: *" >
              <input id="inputDescription" type="text"
                className="form-control"
                name="descricao"
                value={this.state.descricao}
                onChange={this.handleChange}
              />
            </FormGroup>
          </div>
        </div>
        <div className="row">
          <div className="col-md-6">
            <FormGroup id="inputYear" label="Year: *">
              <input id="inputYear"
                type="text"
                name="ano"
                value={this.state.ano}
                onChange={this.handleChange}
                className="form-control" />
            </FormGroup>
          </div>
          <div className="col-md-6">
            <FormGroup id="inputMonth" label="Month: *">
              <SelectMenu id="inputMonth"
                options={months}
                name="mes"
                value={this.state.mes}
                onChange={this.handleChange}
                className="form-control" />
            </FormGroup>
          </div>
        </div>
        <div className="row">
          <div className="col-md-4">
            <FormGroup id="inputValue" label="Value: *">
              <input id="inputValue"
                type="text"
                name="valor"
                value={this.state.valor}
                onChange={this.handleChange}
                className="form-control" />
            </FormGroup>
          </div>
          <div className="col-md-4">
            <FormGroup id="inputType" label="Type: *">
              <SelectMenu id="inputType"
                options={types}
                name="tipo"
                value={this.state.tipo}
                onChange={this.handleChange}
                className="form-control" />
            </FormGroup>
          </div>

          <div className="col-md-4">
            <FormGroup id="inputStatus" label="Status: ">
              <input type="text"
                className="form-control"
                name="status"
                value={this.state.status}
                disabled />
            </FormGroup>
          </div>
        </div>
        <div className="row">
          <div className="col-md-6" >
            {this.state.updating ? (
              <button
                className="btn btn-primary"
                onClick={this.update}>
                <i className="pi pi-refresh"></i> Update
              </button>
            ) : (
                <button
                  className="btn btn-success"
                  onClick={this.submit}>
                  <i className="pi pi-save"></i> Save
                </button>
              )}
            <button
              className="btn btn-danger"
              onClick={this.goToEntries}>
              <i className="pi pi-times"></i> Cancel
            </button>
          </div>
        </div>
      </Card>
    )
  }
}

export default withRouter(AddReact)
