import LocalStorageService from "./LocalStorageService";

export const CURRENCY_USER = "_current_user";

export default class AuthService {
  static isUserAuthenticated() {
    const currentyUser = LocalStorageService.getItem(CURRENCY_USER);
    return currentyUser && currentyUser.id;
  }

  static removeAuthenticatedUser() {
    LocalStorageService.deleteItem(CURRENCY_USER);
  }

  static signIn(user) {
    LocalStorageService.addItem(CURRENCY_USER, user);
  }

  static getAuthenticatedUser() {
    return LocalStorageService.getItem(CURRENCY_USER);
  }
}
