import React from "react";
import "./App.css";

class App extends React.Component {
  state = {
    number1: "",
    number2: "",
    result: "",
  };

  somar = () => {
    const result = parseInt(this.state.number1) + parseInt(this.state.number2);
    this.setState({ result: result });
  };

  render() {
    return (
      <div>
        <label>First number:</label>
        <input
          type="text"
          value={this.state.number1}
          onChange={(e) => this.setState({ number1: e.target.value })}
        />
        <br />
        <label>Second number:</label>
        <input
          type="text"
          value={this.state.number2}
          onChange={(e) => this.setState({ number2: e.target.value })}
        />
        <br />
        <button onClick={this.somar}>Sum</button>
        <br />
        The result is: {this.state.result}
      </div>
    );
  }
}

export default App;
