import React from 'react'
import { withRouter } from 'react-router-dom'
import Card from "../../components/card"
import FormGroup from '../../components/form-group'
import SelectMenu from '../../components/selectMenu'
import EntryList from './entryList'
import EntryService from '../../app/services/entryService'
import LocalStorageService from '../../app/services/localStorageService'
import { errorAlert, successAlert } from "../../components/toastr";

class Entries extends React.Component {

  state = {
    year: '',
    month: '',
    description: '',
    type: '',
    status: '',
    entries: []
  }

  constructor() {
    super()
    this.service = new EntryService()
  }

  validate() {
    if (!this.state.year) {
      errorAlert('Year is required')
      return false
    }

    return true
  }

  search = () => {
    if (!this.validate()) {
      return false
    }

    const currentUser = LocalStorageService.getItem('_usuario_logado')

    this.service.filter({
      usuario: currentUser.id,
      year: this.state.year,
      month: this.state.month,
      description: this.state.description,
      type: this.state.type,
      status: this.state.status
    }).then(response => {
      successAlert('Data fetched!')
      this.setState({ entries: response.data })
    }).catch(error => {
      errorAlert(error.response.data)
    })
  }

  editEntry = (id) => {
    console.log('Editing the entry ', id)
  }

  deleteEntry = (entry) => {
    this.service.deleteEntry(
      entry.id
    ).then(response => {
      this.removeFromEntryList(entry)
      successAlert("Entry deleted!")
    }).catch(error => {
      errorAlert('Failed to delete entry!')
    })
  }

  removeFromEntryList(entry) {
    const entries = this.state.entries
    const entryIndex = entries.indexOf(entry)
    entries.splice(entryIndex, 1)
    this.setState(entries)
  }

  render() {
    const months = this.service.getMonthList()
    const types = this.service.getTypeList()

    return (
      <Card title="Search Entries">
        <div className="row">
          <div className="col-lg-6">
            <div className="bs-component">
              <fieldset>
                <FormGroup label="Ano: *" htmlFor="inputYear">
                  <input type="text"
                    className="form-control"
                    value={this.state.year}
                    onChange={e => this.setState({ year: e.target.value })}
                    id="inputYear"
                    placeholder="Year"
                  />
                </FormGroup>
                <FormGroup label="Month: " htmlFor="inputMonth">
                  <SelectMenu
                    id="inputMonth"
                    value={this.state.month}
                    onChange={e => this.setState({ month: e.target.value })}
                    className="form-control"
                    options={months}
                  />
                </FormGroup>
                <FormGroup label="Description: *" htmlFor="inputDescription">
                  <input type="text"
                    className="form-control"
                    value={this.state.description}
                    onChange={e => this.setState({ description: e.target.value })}
                    id="inputDescription"
                    placeholder="Description"
                  />
                </FormGroup>
                <FormGroup label="Type: " htmlFor="inputType">
                  <SelectMenu
                    id="inputType"
                    value={this.state.type}
                    onChange={e => this.setState({ type: e.target.value })}
                    className="form-control"
                    options={types}
                  />
                </FormGroup>
                <button
                  type="button"
                  className="btn btn-success"
                  onClick={this.search}>
                  Buscar
                  </button>
                <button
                  type="button"
                  className="btn btn-danger">
                  Cadastrar
                  </button>
              </fieldset>
            </div>
          </div>
        </div>
        <br />
        <div className="row">
          <div className="col-lg-12">
            <EntryList
              entries={this.state.entries}
              editAction={this.editEntry}
              deleteAction={this.deleteEntry}
            />
          </div>
        </div>
      </Card>
    )
  }
}

export default withRouter(Entries)
