import React from 'react'
import { withRouter } from 'react-router-dom'
import EntryService from '../../app/services/entryService'
import Card from '../../components/card'
import FormGroup from '../../components/form-group'
import SelectMenu from '../../components/selectMenu'
import LocalStorageService from '../../app/services/localStorageService'
import { successAlert, errorAlert } from '../../components/toastr'

class AddReact extends React.Component {
  state = {
    id: null,
    description: '',
    year: '',
    month: '',
    value: '',
    type: '',
    status: '',
    usuario: ''
  }

  constructor() {
    super()
    this.service = new EntryService()
  }

  componentDidMount() {
    const params = this.props.match.params

    if (params.id) {
      this.service.find(
        params.id
      ).then(response => {
        this.setState({
          id: response.data.id,
          description: response.data.descricao,
          year: response.data.ano,
          month: response.data.mes,
          value: response.data.valor,
          type: response.data.tipo,
          status: response.data.status,
          usuario: response.data.usuario
        })
      }).catch(error => {
        errorAlert(error.response.data)
      })
    }

  }

  handleChange = (event) => {
    const value = event.target.value
    const name = event.target.name

    this.setState({ [name]: value })
  }

  submit = () => {
    const currencyUser = LocalStorageService.getItem('_usuario_logado')
    const { description, year, month, value, type } = this.state
    const entry = {
      descricao: description,
      ano: year,
      mes: month,
      valor: value,
      tipo: type,
      usuario: currencyUser.id
    }
    this.service.save(
      entry
    ).then(response => {
      successAlert('Entry added.')
      this.goToEntries()
    }).catch(error => {
      errorAlert(error.response.data)
    })
  }

  goToEntries = () => {
    this.props.history.push('/entries')
  }

  render() {
    const months = this.service.getMonthList()
    const types = this.service.getTypeList()

    return (
      <Card title="Add Entry">
        <div className="row">
          <div className="col-md-12">
            <FormGroup id="inputDescription" label="Description: *" >
              <input id="inputDescription" type="text"
                className="form-control"
                name="description"
                value={this.state.description}
                onChange={this.handleChange}
              />
            </FormGroup>
          </div>
        </div>
        <div className="row">
          <div className="col-md-6">
            <FormGroup id="inputYear" label="Year: *">
              <input id="inputYear"
                type="text"
                name="year"
                value={this.state.year}
                onChange={this.handleChange}
                className="form-control" />
            </FormGroup>
          </div>
          <div className="col-md-6">
            <FormGroup id="inputMonth" label="Month: *">
              <SelectMenu id="inputMonth"
                options={months}
                name="month"
                value={this.state.month}
                onChange={this.handleChange}
                className="form-control" />
            </FormGroup>
          </div>
        </div>
        <div className="row">
          <div className="col-md-4">
            <FormGroup id="inputValue" label="Value: *">
              <input id="inputValue"
                type="text"
                name="value"
                value={this.state.value}
                onChange={this.handleChange}
                className="form-control" />
            </FormGroup>
          </div>
          <div className="col-md-4">
            <FormGroup id="inputType" label="Type: *">
              <SelectMenu id="inputType"
                options={types}
                name="type"
                value={this.state.type}
                onChange={this.handleChange}
                className="form-control" />
            </FormGroup>
          </div>

          <div className="col-md-4">
            <FormGroup id="inputStatus" label="Status: ">
              <input type="text"
                className="form-control"
                name="status"
                value={this.state.status}
                disabled />
            </FormGroup>
          </div>
        </div>
        <div className="row">
          <div className="col-md-6" >
            <button
              className="btn btn-success"
              onClick={this.submit}>
              <i className="pi pi-save"></i>
              Save
            </button>

            <button
              className="btn btn-danger"
              onClick={this.goToEntries}>
              <i className="pi pi-times"></i>
              Cancel
            </button>
          </div>
        </div>
      </Card>
    )
  }
}

export default withRouter(AddReact)
