import ApiService from '../apiService';

class EntryService extends ApiService {

  constructor() {
    super('/api/lancamentos')
  }

  filter(entry) {
    let filters = `?usuario=${entry.usuario}&ano=${entry.year}`

    if (entry.month) {
      filters = `${this.filter}&mes=${entry.month}`
    }

    if (entry.type) {
      filters = `${filters}&tipo=${entry.type}`
    }

    if (entry.status) {
      filters = `${filters}&status=${entry.status}`
    }

    if (entry.description) {
      filters = `${filters}&descricao=${entry.description}`
    }

    return this.get(filters)
  }

  deleteEntry(id) {
    return this.delete(`/${id}`)
  }
  getMonthList() {
    return [
      { label: 'Choose...', value: '' },
      { label: 'January', value: 1 },
      { label: 'February', value: 2 },
      { label: 'March', value: 3 },
      { label: 'April', value: 4 },
      { label: 'May', value: 5 },
      { label: 'June', value: 6 },
      { label: 'July', value: 7 },
      { label: 'August', value: 8 },
      { label: 'September', value: 9 },
      { label: 'October', value: 10 },
      { label: 'November', value: 11 },
      { label: 'December', value: 12 }
    ]
  }

  getTypeList() {
    return [
      { label: 'Choose...', value: '' },
      { label: 'Despesa', value: 'DESPESA' },
      { label: 'Receita', value: 'RECEITA' }
    ]
  }
}
export default EntryService
