import React from "react";
import { HashRouter, Switch, Route, Redirect } from "react-router-dom";
import Login from "../views/login";
import CadastroUsuario from "../views/cadastroUsuario";
import Home from "../views/home";
import Entries from "../views/entries/entries";
import addEntry from "../views/entries/addEntry";
import { AuthConsumer } from "./authProvider";

function AuthRoute({ component: Component, isUserAuthenticated, ...props }) {
  return (
    <Route
      {...props}
      render={(componentProps) => {
        if (isUserAuthenticated) {
          return <Component {...componentProps} />;
        } else {
          return (
            <Redirect
              to={{
                pathname: "/login",
                state: { from: componentProps.location },
              }}
            />
          );
        }
      }}
    />
  );
}

function Rotas(props) {
  return (
    <HashRouter>
      <Switch>
        <Route path="/cadastro-usuario" component={CadastroUsuario} />
        <Route path="/login" component={Login} />
        <AuthRoute
          isUserAuthenticated={props.isUserAuthenticated}
          path="/home"
          component={Home}
        />
        <AuthRoute
          isUserAuthenticated={props.isUserAuthenticated}
          path="/entries"
          component={Entries}
        />
        <AuthRoute
          isUserAuthenticated={props.isUserAuthenticated}
          path="/add-entry/:id?"
          component={addEntry}
        />
      </Switch>
    </HashRouter>
  );
}

export default () => (
  <AuthConsumer>
    {(context) => <Rotas isUserAuthenticated={context.isAuthenticated}></Rotas>}
  </AuthConsumer>
);
